import React, { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch,
} from 'react-router-dom';

import './App.css';
import { AuthContext } from './shared/context/auth-context';
import MainNavigation from './shared/components/Navigation/MainNavigation';
import Players from './players/pages/Players';
import Auth from './user/pages/Auth';
import NewPlayer from './players/pages/NewPlayer';
import PlayerDetails from './players/pages/PlayerDetails';

import { MOCK_PLAYERS } from './util/MockPlayers';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const login = () => {
    console.log('LOGING IN!');
    setIsLoggedIn(true);
  };

  const logout = () => {
    console.log('LOGING OUT!');
    setIsLoggedIn(false);
  };

  const rankPlayers = () => {
    MOCK_PLAYERS.sort(function (a, b) {
      return b.points - a.points;
    });

    MOCK_PLAYERS.forEach((player, index) => (player.ranking = index + 1));
  };

  rankPlayers();

  let routes;

  routes = (
    <Switch>
      <Route path="/" exact>
        <Players />
      </Route>
      <Route path="/auth" exact>
        <Auth />
      </Route>
      <Route path="/players/new" exact>
        <NewPlayer />
      </Route>
      <Route path="/player/:pid" exact>
        <PlayerDetails />
      </Route>
      <Redirect to="/" />
    </Switch>
  );

  return (
    <AuthContext.Provider
      value={{
        isLoggedIn: isLoggedIn,
        login: login,
        logout: logout,
        tennisPlayers: MOCK_PLAYERS,
      }}
    >
      <Router>
        <MainNavigation />
        <main>{routes}</main>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
