import React, { useContext, useState, useEffect } from 'react';
import { useHistory, useParams } from 'react-router-dom';

import './NewPlayer.css';
import { useForm } from '../../shared/hooks/form-hook';
import Input from '../../shared/components/FormElements/Input';
import Button from '../../shared/components/FormElements/Button';
import Card from '../../shared/components/UIElements/Card';
import { VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from '../../util/validators';
import { AuthContext } from '../../shared/context/auth-context';
import { MOCK_PLAYERS } from '../../util/MockPlayers';

const UpdatePlayer = () => {
  const [loadedPlayer, setLoadedPlayer] = useState(null);
  const playerId = useParams().pid;
  const history = useHistory();
  const auth = useContext(AuthContext);
  const [formState, inputHandler, setFormData] = useForm(
    {
      age: {
        value: null,
        isValid: false,
      },
      points: {
        value: null,
        isValid: false,
      },
      weight: {
        value: null,
        isValid: false,
      },
      height: {
        value: null,
        isValid: false,
      },
      grand_slams: {
        value: null,
        isValid: true,
      },
    },
    false
  );

  //   useEffect(() => {
  //     const getPlayer = () => {
  //       const foundPlayer = auth.tennisPlayers.filter(
  //         (player) => player.id === playerId
  //       );
  //       setLoadedPlayer(foundPlayer);
  //     };
  //     getPlayer();
  //     console.log('LOADED PLAYER IS ' + loadedPlayer);
  //   }, [loadedPlayer, playerId, auth.tennisPlayers]);

  const foundPlayer = MOCK_PLAYERS.filter((player) => player.id === playerId);

  useEffect(() => {
    setLoadedPlayer(foundPlayer);
  }, [foundPlayer]);

  const formSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formState);

    history.push(`/player/${playerId}`);
  };

  return (
    <React.Fragment>
      <h2 className="player-title">Update Player</h2>
      <form className="player-form" onSubmit={formSubmitHandler}>
        <Input
          id="age"
          onInput={inputHandler}
          validators={[VALIDATOR_REQUIRE()]}
          element="input"
          type="number"
          label="Age"
          errorText="Please enter player's age"
          initialValue={auth.tennisPlayers.age}
          initialValid={true}
        />
        <Input
          id="points"
          onInput={inputHandler}
          validators={[VALIDATOR_REQUIRE()]}
          element="input"
          type="number"
          label="Points"
          errorText="Please enter player's points"
        />
        <Input
          id="weight"
          onInput={inputHandler}
          validators={[VALIDATOR_REQUIRE()]}
          element="input"
          type="number"
          label="Weight"
          errorText="Please enter player's weight"
        />
        <Input
          id="height"
          onInput={inputHandler}
          validators={[VALIDATOR_REQUIRE()]}
          element="input"
          type="number"
          label="Height"
          errorText="Please enter player's height"
        />
        <Input
          id="grand_slams"
          onInput={inputHandler}
          validators={[]}
          element="input"
          type="number"
          label="Grand Slams Won"
        />
        <Button type="submit" disabled={!formState.isValid}>
          UPDATE PLAYER
        </Button>
      </form>
    </React.Fragment>
  );
};

export default UpdatePlayer;
