import React, { useContext, useState } from 'react';
import { useParams } from 'react-router-dom';

import { AuthContext } from '../../shared/context/auth-context';
import Button from '../../shared/components/FormElements/Button';
import Card from '../../shared/components/UIElements/Card';
import './PlayerDetails.css';

const PlayerDetails = () => {
  const [showModal, setShowModal] = useState(false);
  const auth = useContext(AuthContext);
  const playerId = useParams().pid;

  const showDeleteWarning = () => {
    setShowModal(true);
  };

  const cancelDeleteHandler = () => {
    setShowModal(false);
  };

  const deletePlayer = () => {};

  const selectedPlayer = auth.tennisPlayers.filter(
    (player) => player.id === playerId
  )[0];

  if (!selectedPlayer) {
    return (
      <div className="center">
        <Card className="card-medium">No players found.</Card>
      </div>
    );
  }

  return (
    <React.Fragment>
      <Modal show={showModal} onCancel={cancelDeleteHandler}></Modal>
      <div className="player-wrapper">
        <Card className="player-item__content">
          <div className="player-item__image">
            <img src={selectedPlayer.image} alt={selectedPlayer.name} />
          </div>
          <div className="player-item__info">
            <h1>{selectedPlayer.name}</h1>
            <h2>{selectedPlayer.country}</h2>
            <p>Age: {selectedPlayer.age}</p>
            <p>Grand Slam titles: {selectedPlayer.grand_slams}</p>
            <p>Grand Slam titles: {selectedPlayer.grand_slams}</p>
            <p>Current ATP rank: {selectedPlayer.ranking}</p>
            <p>Current ATP points: {selectedPlayer.points}</p>
          </div>
          <div className="player-item__actions">
            <Button>EDIT</Button>
            <Button danger>DELETE</Button>
          </div>
        </Card>
      </div>
    </React.Fragment>
  );
};

export default PlayerDetails;
